{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyOM2BZIYIH7Zu4yQCLL8waE"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","source":["%%writefile app.py\n","import streamlit as st\n","import pandas as pd\n","import joblib\n","\n","st.title(\" Titanic Survival Prediction App\")\n","st.write(\"Fill the information below to predict survival probability.\")\n","\n","# Load model & scaler\n","model = joblib.load(\"titanic_model.pkl\")\n","scaler = joblib.load(\"titanic_scaler.pkl\")\n","\n","# User inputs\n","Pclass = st.selectbox(\"Passenger Class\", [1, 2, 3])\n","Sex = st.selectbox(\"Sex\", [\"male\", \"female\"])\n","Age = st.slider(\"Age\", 1, 90, 30)\n","SibSp = st.number_input(\"Siblings/Spouses Aboard\", 0, 10, 0)\n","Parch = st.number_input(\"Parents/Children Aboard\", 0, 10, 0)\n","Fare = st.number_input(\"Fare\", 0.0, 600.0, 50.0)\n","Embarked = st.selectbox(\"Port of Embarkation\", [\"C\", \"Q\", \"S\"])\n","\n","# Form dataframe\n","data = pd.DataFrame({\n","\"Pclass\":[Pclass],\n","\"Sex\":[Sex],\n","\"Age\":[Age],\n","\"SibSp\":[SibSp],\n","\"Parch\":[Parch],\n","\"Fare\":[Fare],\n","\"Embarked\":[Embarked]\n","})\n","\n","# One hot encoding\n","data = pd.get_dummies(data, drop_first=True)\n","\n","# Required columns (same as model training)\n","required_cols = ['Pclass','Age','SibSp','Parch','Fare',\n","'Sex_male','Embarked_Q','Embarked_S']\n","\n","# Add missing columns\n","for col in required_cols:\n","    if col not in data.columns:\n","        data[col] = 0\n","\n","data = data[required_cols]\n","\n","# Scaling\n","data_scaled = scaler.transform(data)\n","\n","# Prediction\n","pred = model.predict(data_scaled)[0]\n","prob = model.predict_proba(data_scaled)[0][1]\n","\n","st.write(\"###Prediction Result:\")\n","st.write(\" Survived\" if pred == 1 else \" Did Not Survive\")\n","\n","st.write(\"###Survival Probability:\")\n","st.write(f\"**{prob:.3f}**\")"],"metadata":{"colab":{"base_uri":"https://localhost:8080/"},"id":"nxhv4il8gpE_","executionInfo":{"status":"ok","timestamp":1765478176310,"user_tz":-330,"elapsed":26,"user":{"displayName":"Pranav","userId":"06820756394112995730"}},"outputId":"fef23ba0-4798-49c7-bf72-ef12654dcfd4"},"execution_count":39,"outputs":[{"output_type":"stream","name":"stdout","text":["Overwriting app.py\n"]}]}]}